<?php class Magestore_Raffle_Block_Adminhtml_Raffle_Renderer_Winnumbers extends Mage_Adminhtml_Block_Widget_Grid_Column_Renderer_Abstract{    public function render(Varien_Object $row){		$ticketId = $row->getTicketId();		$winnumbers = Mage::getModel('raffle/randnum')->getCollection()->addFieldToFilter('ticket_id',$ticketId);		$winnumbers->getSelect()            			->joinLeft(                				array('prizes'=>Mage::getModel('core/resource')->getTableName('raffle_prizes')),                "main_table.prize_id = prizes.prize_id",				array('price'=>'prizes.price')        				);		$winNumbers = '';			foreach($winnumbers as $_winnumber){			$winNumbers .= $_winnumber->getNumber().':'.Mage::helper('core')->currency($_winnumber->getPrice()).', ';		}        				if($ticketId){			return $winNumbers;			}        				return '';		}	}